import React, { useState, useEffect } from 'react';
import { saveDonation, getTotalDonations, getDonationCount } from '../services/firebase';
import './DonationTracker.css';

const DonationTracker: React.FC = () => {
  const [amount, setAmount] = useState<number>(0);
  const [donorName, setDonorName] = useState<string>('');
  const [totalDonations, setTotalDonations] = useState<number>(0);
  const [donationCount, setDonationCount] = useState<number>(0);
  const [isSubmitting, setIsSubmitting] = useState<boolean>(false);
  const [message, setMessage] = useState<string>('');

  useEffect(() => {
    // Subscribe to donation data
    getTotalDonations((total) => {
      setTotalDonations(total);
    });

    getDonationCount((count) => {
      setDonationCount(count);
    });
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (amount <= 0) {
      setMessage('Please enter a valid donation amount');
      return;
    }

    setIsSubmitting(true);
    setMessage('');

    const donationData = {
      amount,
      timestamp: Date.now(),
      ...(donorName && { donor: donorName })
    };

    const success = await saveDonation(donationData);

    if (success) {
      setMessage('Thank you for your donation!');
      setAmount(0);
      setDonorName('');
    } else {
      setMessage('There was an error processing your donation. Please try again.');
    }

    setIsSubmitting(false);
  };

  return (
    <div className="donation-tracker">
      <h2>Water Weight Donation Tracker</h2>

      <div className="stats">
        <div className="stat-box">
          <h3>Total Raised</h3>
          <p>${totalDonations.toFixed(2)}</p>
        </div>
        <div className="stat-box">
          <h3>Number of Donations</h3>
          <p>{donationCount}</p>
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="amount">Donation Amount ($)</label>
          <input
            type="number"
            id="amount"
            value={amount || ''}
            onChange={(e) => setAmount(Number(e.target.value))}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="donor">Your Name (optional)</label>
          <input
            type="text"
            id="donor"
            value={donorName}
            onChange={(e) => setDonorName(e.target.value)}
          />
        </div>

        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Processing...' : 'Donate Now'}
        </button>

        {message && <p className="message">{message}</p>}
      </form>
    </div>
  );
};

export default DonationTracker;
